import React, { Component } from "react";
import axios from "axios";
import styled from "styled-components";
import ImageInGallery from "./ImageInGallery";

const GalleryWrapper = styled.div`
  display: grid;
  /* padding: 15px; */
  border: 2px solid black;
  grid-template-columns: 1fr 1fr;
`;

export default class GalleryPagination extends Component {
  constructor(props) {
    super(props);

    this.state = {
      searchField: "",
      imagesList: null,
      imageDiv: false,
      currentImage: null,
      apikey: "221b4e10-7bc5-11ea-8865-8f439f2941d8",
      page: 1,
      info: null,
    };
    this.nextPage = this.nextPage.bind(this);
  }

  nextPage() {
    axios
      .get(this.state.next)
      .then((res) => res.data)
      .then((data) =>
        this.setState({
          imagesList: data.records,
          next: data.info.next,
          prev: data.info.prev,
        })
      );
  }

  prevPage() {
    axios
      .get(this.state.next)
      .then((res) => res.data)
      .then((data) =>
        this.setState({
          imagesList: data.records,
          next: data.info.next,
          prev: data.info.prev,
        })
      );
  }

  componentDidMount() {
    console.log(this.state.page);

    axios
      .get(
        `https://api.harvardartmuseums.org/object?size=10&page=${this.state.page}&apikey=${this.state.apikey}`
      )
      .then((res) => res.data)
      .then((data) => {
        this.setState({
          imagesList: data.records,
          next: data.info.next,
          prev: data.info.prev,
        });
        console.log(data.info.next);
      });
  }

  render() {
    return (
      <div>
        <GalleryWrapper>
          {this.state.imagesList === null ? (
            <h1>Loading...</h1>
          ) : (
            this.state.imagesList.map((item) =>
              item.primaryimageurl && item.primaryimageurl != null ? (
                <ImageInGallery src={item.primaryimageurl} />
              ) : null
            )
          )}
          {() => console.log(this.state.info)}
          {/* {setTimeout(() => {
            this.state.info.hasOwnProperty("next") ? (
              
            ) : null;
          }, 1000)} */}
          {/* {this.state.info.prev ? (
            <button onClick={() => this.prevPage()}>PREV PAGE</button>
          ) : null} */}
        </GalleryWrapper>
        <button onClick={this.nextPage}>NEXT PAGE</button>
        <button onClick={() => this.prevPage()}>PREV PAGE</button>
      </div>
    );
  }
}
