import React, { Component } from "react";
import axios from "axios";

class Gallery extends Component {
	constructor() {
		super();
		this.state = {
			searchField: "",
			imagesList: null,
		};
	}

	componentDidMount() {
		axios
			.get("https://jsonplaceholder.typicode.com/photos/")
			.then((res) => res.data)
			.then((data) => this.setState({ imagesList: data[0] }));
		console.log(this.state.imagesList);
	}

	onSearchChange = (event) => {
		this.setState({ searchField: event.target.value });
	};

	render() {
		return (
			<div>
				<input type="search" placeholder="Search" onChange={this.onSearchChange}></input>

				{this.state.imagesList
					.filter((item) => {
						return this.state.searchField === "" || item.character.toLocaleLowerCase().includes(this.state.searchField.toLocaleLowerCase());
					})
					.map((item) => {
						return (
							<div>
								<h4>{item.character}</h4>
								<img src={item.image}></img>
							</div>
						);
					})}
			</div>
		);
	}
}

export default Gallery;
