import React from "react";

export default class ThisTest extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "Version Uno ",
      style: false,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState((state) => ({
      style: !state.style,
      title: "Version Due",
    }));
  }

  render() {
    return (
      <div className={this.state.style ? "classOne" : "classTwo"}>
        <button onClick={this.handleClick}> Fade that shit </button>
        {this.state.title}
      </div>
    );
  }
}
