import React, { Component } from "react";

export default class FullImage extends Component {
	render() {
		return (
			<div style={{ display: this.state.imageDiv ? "flex" : "none" }} className="imageDiv">
				<div>
					<button onClick={this.showImage}>Close</button>
				</div>

				<div>
					<img className="highlightImg" alt="idk" src={this.state.currentImage && this.state.currentImage.primaryimageurl}></img>
				</div>
			</div>
		);
	}
}
