import React from "react";

export default class ThisTest extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "Version Uno ",
      style: false,
    };
  }

  render() {
    return (
      <div className={this.state.style ? "classOne" : "classTwo"}>
        <button onClick={this.setState((this.style = "true"))}> </button>
        {this.state.title}
      </div>
    );
  }
}
