import React from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import axios from "axios";
import "./galleryscroll.css";
class GalleryScroll extends React.Component {
  state = {
    images: [],
    pageNumber: 1,
    items: 20,
    hasMore: true,
  };

  componentDidMount() {
    //initial request is sent
    this.fetchData();
  }

  fetchData = () => {
    axios
      .get(
        `https://api.harvardartmuseums.org/object?apikey=627d7fb0-789a-11ea-b0f5-f14efbeacef8&size=${this.state.items}&page=${this.state.pageNumber}`
      )
      .then((res) =>
        this.setState({
          //updating data
          images: [...this.state.images, ...res.data.records],
          //updating page numbers
          pageNumber: this.state.pageNumber + 1,
        })
      );
  };

  render() {
    return (
      <div>
        <InfiniteScroll
          dataLength={this.state.images.length} //This is important field to render the next data
          next={this.fetchData}
          hasMore={this.state.hasMore}
          loader={<img src="src\components\91.svg" alt="loading..." />}
        >
          <div className="image-grid">
            {this.state.images.map((image) => (
              <div className="artsyCard">
                <img
                  className="image"
                  src={image.primaryimageurl}
                  key={image.rank}
                  alt=""
                />
                <div className="overlay">
                  <div className="text">{image.title}</div>
                </div>
              </div>
            ))}
          </div>
        </InfiniteScroll>
      </div>
    );
  }
}

export default GalleryScroll;
