import React, { Component } from "react";
import axios from "axios";
import styled from "styled-components";
import ImageInGallery from "./ImageInGallery";
import FilterItem from "./FilterItem";
import Filters from "./Filters";

const GalleryWrapper = styled.div`
  display: grid;
  /* padding: 15px; */
  border: 2px solid black;
  grid-template-columns: 1fr 1fr;
`;

const StyledUl = styled.ul`
  height: 250px;
  overflow: scroll;
`;

export default class GalleryPagination extends Component {
  constructor(props) {
    super(props);

    this.state = {
      searchField: "",
      imagesList: null,
      imageDiv: false,
      currentImage: null,
      apikey: "221b4e10-7bc5-11ea-8865-8f439f2941d8",
      page: 1,
      info: null,
      categories: [],
    };
    this.nextPage = this.nextPage.bind(this);
    this.prevPage = this.prevPage.bind(this);
    this.filterCall = this.filterCall.bind(this);
  }
  filterCall(event) {
    console.log(event.target.value);
    axios
      .get(
        `https://api.harvardartmuseums.org/object?size=20&page=1&apikey=${this.state.apikey}&classification=${event.target.innerText}&hasimage=1`
      )
      .then((res) => res.data)
      .then((data) =>
        this.setState({
          imagesList: data.records,
          next: data.info.next,
          prev: data.info.prev,
        })
      );
  }

  nextPage() {
    axios
      .get(this.state.next)
      .then((res) => res.data)
      .then((data) =>
        this.setState({
          imagesList: data.records,
          next: data.info.next,
          prev: data.info.prev,
        })
      );
  }

  prevPage() {
    axios
      .get(this.state.prev)
      .then((res) => res.data)
      .then((data) =>
        this.setState({
          imagesList: data.records,
          next: data.info.next,
          prev: data.info.prev,
        })
      );
  }

  componentDidMount() {
    console.log(this.state.page);

    axios
      .get(
        `https://api.harvardartmuseums.org/object?size=20&page=${this.state.page}&apikey=${this.state.apikey}`
      )
      .then((res) => res.data)
      .then((data) => {
        this.setState({
          imagesList: data.records,
          next: data.info.next,
          prev: data.info.prev,
        });
        console.log(data.info.next);
      });
    axios
      .get(
        `https://api.harvardartmuseums.org/classification?size=100&page=1&apikey=${this.state.apikey}`
      )
      .then((res) => res.data)
      .then((data) =>
        data.records.map((e) => {
          this.setState({ categories: [...this.state.categories, e.name] });
          console.log(this.state.categories);
        })
      );
  }

  render() {
    return (
      <div>
        <ul>
          {this.state.categories.map((e) => (
            <FilterItem name={e} function={this.filterCall}></FilterItem>
          ))}
        </ul>

        <GalleryWrapper>
          {this.state.imagesList === null ? (
            <h1>Loading...</h1>
          ) : (
            this.state.imagesList.map((item) =>
              item.primaryimageurl &&
              item.primaryimageurl != null &&
              item.images.length > 0 ? (
                <ImageInGallery src={item.primaryimageurl} />
              ) : null
            )
          )}
          {() => console.log(this.state.info)}
        </GalleryWrapper>
        <button onClick={this.nextPage}>NEXT PAGE</button>
        <button onClick={() => this.prevPage()}>PREV PAGE</button>
      </div>
    );
  }
}
