import React, { Component } from "react";
import axios from "axios";
import "./Gallery.css";

class Gallery extends Component {
	constructor() {
		super();
		this.state = {
			searchField: "",
			imagesList: null,
			imageDiv: false,
		};
	}

	componentDidMount() {
		axios
			.get("https://api.harvardartmuseums.org/object?size=100&page=88&apikey=221b4e10-7bc5-11ea-8865-8f439f2941d8")
			.then((res) => res.data)
			.then((data) => this.setState({ imagesList: data.records }));
	}

	onSearchChange = (event) => {
		this.setState({ searchField: event.target.value });
	};

	showImage = () => {
		this.setState({ imageDiv: !this.state.imageDiv });
		setTimeout(() => {
			document.body.style.overflowY = this.state.imageDiv ? "hidden" : "scroll";
		}, 100);
	};

	render() {
		return (
			<div>
				<div style={{ display: this.state.imageDiv ? "block" : "none" }} className="imageDiv">
					<button onClick={this.showImage}>Close</button>
				</div>
				<div className="container">
					<div className="search">
						<input type="search" placeholder="Search" onChange={this.onSearchChange}></input>
					</div>

					{this.state.imagesList === null ? (
						<h1>Loading...</h1>
					) : (
						this.state.imagesList
							.filter((item) => {
								if (primaryimageurl in item) {
									return (
										this.state.searchField === "" || item.title.toLocaleLowerCase().includes(this.state.searchField.toLocaleLowerCase())
									);
								}
							})
							.map((item) => {
								return (
									<div className="photo">
										<div className="heading">
											<h4>{item.title}</h4>
										</div>

										<img onClick={this.showImage} alt="placeholder" src={item.primaryimageurl}></img>
									</div>
								);
							})
					)}
				</div>
			</div>
		);
	}
}

export default Gallery;
