import React, { Component } from "react";
import axios from "axios";
import "./Artists.css";

class Artists extends Component {
	constructor() {
		super();
		this.state = {
			searchField: "",
			artistsList: null,
		};
	}

	componentDidMount() {
		axios
			.get("https://api.harvardartmuseums.org/object?size=100&page=888&apikey=221b4e10-7bc5-11ea-8865-8f439f2941d8")
			.then((res) => res.data)
			.then((data) => this.setState({ artistsList: data.records }));
	}

	onSearchChange = (event) => {
		this.setState({ searchField: event.target.value });
	};

	render() {
		return (
			<div>
				<div className="container">
					<div className="search">
						<input type="search" placeholder="Search" onChange={this.onSearchChange}></input>
					</div>

					{this.state.artistsList === null ? (
						<h1>Loading...</h1>
					) : (
						this.state.artistsList
							.filter((obj) => {
								if (obj.hasOwnProperty("people")) {
									return (
										this.state.searchField === "" ||
										obj.people[0].name.toLocaleLowerCase().includes(this.state.searchField.toLocaleLowerCase())
									);
								} else {
									return null;
								}
							})
							.map((obj) => {
								return (
									<div className="names">
										<h1>{obj.people[0].name}</h1>
									</div>
								);
							})
					)}
				</div>
			</div>
		);
	}
}

export default Artists;
