import React, { Component } from "react";
import { Masonry } from "masonic";
import axios from "axios";
import styled from "styled-components";
import ImageInGallery from "./ImageInGallery";
import FilterItem from "./FilterItem";
import Filters from "./Filters";
import { categs } from "./categs";

export default class Masonic extends Component {
  constructor(props) {
    super(props);

    this.state = {
      searchField: "",
      imagesList: null,
      imageDiv: false,
      currentImage: null,
      apikey: "221b4e10-7bc5-11ea-8865-8f439f2941d8",
      page: 1,
      info: null,
      categories: categs,
      showCategories: false,
    };
  }

  componentDidMount() {
    axios
      .get(
        `https://api.harvardartmuseums.org/object?size=20&page=${this.state.page}&apikey=${this.state.apikey}`
      )
      .then((res) => res.data)
      .then((data) => {
        this.setState({
          imagesList: data.records,
          next: data.info.next,
          prev: data.info.prev,
          items: data.records.map((item) => (
            <ImageInGallery src={item.primaryimageurl} />
          )),
        });
        console.log(data.info.next);
      });
  }

  render() {
    return <Masonry items={this.state.items} />;
  }
}
